#include<map>#include<iostream>using namespace std;int main(){	map<string, int> Word;	//definition	map<string, int>::iterator it_word;//define a iterator of map	//accessing value	it_word = Word.begin();	cout << it_word->first << "--- " << it_word->second << endl;	Word["Hello"] = 1;  //if key exist, access its value; if not, a new key was inserted	//finding key	if (1 != Word["Hello"])	//dangerous, new entry may be inserted		it_word = Word.find("Hello");	if (it_word != Word.end())//found it		cout << "found\n";	int cnt = Word.count("Hello");	if (0 != cnt);		//found it}