#include<iostream>#include<vector>using namespace std;vector<int>* fibon_seq(int iLength){    if (iLength <= 0 || iLength >= 1024)    {        cerr << "Length" << iLength            << not "supported, reset to 8"            << endl;        iLength = 8;    }    static vector<int> Elems(iLength);    for (int iX = 0; iX < iLength; iX++)    {        if (iX == 0 || iX == 1)            Elems[iX] = 1;        else            Elems[iX] = Elems[iX - 1] + Elems[iX - 2];    }    return &Elems;}int main(){    vector<int>* fibSeq = fibon_seq(10);    for (int num : *fibSeq)    {        cout << num << " ";    }    cout << endl;    // 释放动态分配的内存    delete fibSeq;    return 0;}